# What is the address of the P2SH script wrapping the witness redeem script in this transaction
 transaction=""

decode=$(bitcoin-cli -regtest decoderawtransaction $transaction)
redeem_script=$(echo $decode | jq -r '.vin[0].scriptSig.asm | split(" ")[-1]')
address=$(bitcoin-cli -regtest decodescript $redeem_script | jq -r '.segwit."p2sh-segwit"')
echo $address
